chrome.runtime.onInstalled.addListener((()=>{chrome.contextMenus.create({id:"pennCourseSearch",title:"Penn Course Search",contexts:["selection"]})})),chrome.contextMenus.onClicked.addListener(((e,t)=>{if("pennCourseSearch"===e.menuItemId){const t=/[^\w\s]/g,n=e.selectionText.replace(t," ").trim().toLocaleUpperCase().split(/ /),o={inputcourse:n[0]+"-"+n[1]};chrome.storage.local.set(o,(()=>{console.log("Input course saved")}))}}));