chrome.runtime.onInstalled.addListener((()=>{chrome.contextMenus.create({id:"pennCourseSearch",title:"Penn Course Search",contexts:["selection"]})})),chrome.contextMenus.onClicked.addListener(((e,t)=>{if("pennCourseSearch"===e.menuItemId){let t=e.selectionText;t=(new DOMParser).parseFromString(`<!doctype html><body>${t}`,"text/html").body.textContent;const o=/[^\w\s]/g,n=t.replace(o," ").trim().toLocaleUpperCase().split(/ /),r={inputcourse:n[0]+"-"+n[1]};chrome.storage.local.set(r,(()=>{console.log("Input course saved")}))}}));